import React, { useState } from "react";
import Link from 'next/link'
import { useDispatch } from "react-redux";
import Alert from "../../../shared/Alert";
import { sortedLastIndex } from "lodash";
import PropTypes from 'prop-types';
import withStyles from 'react-jss'
import axios from 'axios'
import { MdCropSquare } from "react-icons/md";

const styles = theme => ({
  root: {
    margin: 20,
  },
  signInButton: {
    marginTop: 10,
    backgroundColor: '#e7e7e7',
    padding: '8px 32px',
    borderRadius: '4px'
  },
  inputField: {
    marginTop: 6
  }
})

const LoginForm = ({
  classes,
}) => {
  //let history = useHistory();
  //const dispatch = useDispatch();

  const [displayName, setDisplayName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const onLogin = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/users/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          displayName: displayName,
          password: password,
        }),
      });
      const data = await response.json();

      console.log(data)


      if (data.msg === "Please enter correct credentials") {
        setError(data.msg);
        window.localStorage.removeItem("token");
      } else if (data.activated === false) {
        window.localStorage.removeItem("token");
        setVerified(true);
      } else {
        window.localStorage.setItem("token", JSON.stringify(data.token));
        dispatch({ type: "LOGIN", payload: true });
        //socket.emit("login");
        //history.push("/");
      }


      //const { mnemonic } = data;

      /*
      // handle error response
      const createWalletData = await fetch("users/createWallet", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          mnemonic: mnemonic,
          password,
        }),
      });


      const wifJson = await createWalletData.json();
      console.log("wifJson", wifJson);

      localStorage.setItem("userAddress", JSON.stringify(wifJson.wif));
      */
     console.log("success")
    } catch (err) {
      console.log("onLogin error" + err);
    }
  };

  const onFormSubmit = (e) => {
    e.preventDefault();
    onLogin();
  };

  return(
    <div className={classes.root}>
      <div className="card-header">
          <h3>Sign In</h3>
      </div>
      <form onSubmit={onFormSubmit}>
        <div className={classes.inputField}>
          <input
            placeholder="Display Name"
            className="form-control"
            onChange={(e) => {
              setDisplayName(e.target.value.trim());
            }}
          />
        </div>
        <div className={classes.inputField}>
          <input
            type="password"
            placeholder="Password"
            className="form-control"
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
        </div>
        <div className="form-group form-check rememberMeSection">
          <input type="checkbox" id="html1" />
          <label htmlFor="html1">Remember me</label>
        </div>
        <div className="login-alert">
          {error.length > 0 ? (
            <Alert
              content={error}
              setAlert={() => {
                setError("");
              }}
            />
          ) : null}
        </div>
        <button className={classes.signInButton}>Sign in</button>
      </form>
    </div>
  )
}



LoginForm.propTypes = {
  classes: PropTypes.object.isRequired,
}

export default withStyles(styles, { injectTheme: true }) (LoginForm);
